import React from 'react';
import './puzzle.styles.scss';

import Board from '../board/board.component';
import Popup from '../popup/popup.component';

import Adapter from '../../api/ApiAdapter';

class Puzzle extends React.Component {

    constructor(props) {
        super(props);
        this.state = {
            settings: true,
            mapWidth: 0,
            mapHeight: 0,
            inputWidth: 0,
            inputHeight: 0,
            startI: 0,
            startJ: 0,
            goalI: 0,
            goalJ: 0,
            costs: [],
            paths: [],
            // start: {i: 0, j: 0},
            // goal: {i: 0, j: 0},
        }
    }

    componentDidMount() {
        // this.setState({mapWidth: 8});
    }

    handleSubmit = async () => {
        // console.log(e);
        // this.setState({
        //     settings: false,
        //     mapWidth: config.mapWidth,
        //     mapHeight: config.mapHeight,
        //     start: config.start,
        //     goal: config.goal,
        //     costs: config.costs
        // });
        // this.setState({mapWidth: this.state.inputWidth});
        // console.log('121321421412');
        // console.log(this.state.inputWidth);
        // this.setState({settings: false, mapWidth: this.state.inputWidth, mapHeight: this.state.inputHeight});
        this.setState({settings: false});
        const resCreateMap = await Adapter.createMap(this.state.inputHeight, this.state.inputWidth);
        const resCreateStart = await Adapter.createStart(this.state.startI, this.state.startJ);
        const resCreateGoal = await Adapter.createGoal(this.state.goalI, this.state.goalJ);
        const resGetPath = await Adapter.getPath();
        // console.log(resCreateMap);
        // console.log(resCreateStart);
        // console.log(resCreateGoal);
        // console.log(resGetPath);
        this.setState({
            // settings: false,
            mapWidth: resCreateMap.mapWidth, 
            mapHeight: resCreateMap.mapHeight,
            startI: resCreateStart.i,
            startJ: resCreateStart.j,
            // start: resCreateStart,
            // goal: resCreateGoal,
            goalI: resCreateGoal.i,
            goalJ: resCreateGoal.j,
            paths: resGetPath.paths
        });
        // console.log(resCreateMap.mapWidth);
        // console.log(this.state.mapWidth);
        // console.log(this.state.goalI);
    }

    changeMap = async () => {
        console.log('called');
        const resCreateMap = await Adapter.createMap(this.state.inputHeight, this.state.inputWidth);
        const resCreateStart = await Adapter.createStart(0, 0);
        const resCreateGoal = await Adapter.createGoal(this.state.inputHeight-1, this.state.inputWidth-1);
        const resGetPath = await Adapter.getPath();

        this.setState({
            mapWidth: resCreateMap.mapWidth, 
            mapHeight: resCreateMap.mapHeight,
            startI: resCreateStart.i,
            startJ: resCreateStart.j,
            goalI: resCreateGoal.i,
            goalJ: resCreateGoal.j,
            paths: resGetPath.paths
        });
        // console.log(resCreateMap);
    }

    handleChange = e => {
        // console.log(e.target);
        const {name, value} = e.target;
        this.setState({[name]: value});
        // console.log(this.state.mapWidth);
    }

    render() {
        // console.log(this.state.settings);
        // const width = this.state.mapWidth;
        // const height = this.state.mapHeight;
        const start = {i: this.state.startI, j: this.state.startJ};
        const goal = {i: this.state.goalI, j: this.state.goalJ};
        return(
            <div>
                {this.state.settings ?
                    <Popup 
                        handleSubmit={this.handleSubmit} handleChange={this.handleChange} />
                    :
                    <div>
                        <Board width={this.state.mapWidth} 
                            height={this.state.mapHeight} 
                            start={start} 
                            goal={goal}
                            paths={this.state.paths}
                        />
                        {/* <input name='inputWidth' type='number' onChange={this.handleChange} required />
                        <input name='inputHeight' type='number' onChange={this.handleChange} required /> */}
                        <div className='config'>
                            <div>
                                <span>Map:</span>
                                <form id='changeMap' onSubmit={this.changeMap}>
                                    <span>i:</span>
                                    <input name='inputHeight' type='number' onChange={this.handleChange} required />
                                    <span>j:</span>
                                    <input name='inputWidth' type='number' onChange={this.handleChange} required />
                                </form>
                                <button type='submit' form='changeMap' value='Submit'>SUBMIT</button>
                            </div>
                            <div>
                                <span>Start:</span>
                                <form id='changeStart' onSubmit={this.changeStart}>
                                    <span>i:</span>
                                    <input name='startI' type='number' onChange={this.handleChange} required />
                                    <span>j:</span>
                                    <input name='startJ' type='number' onChange={this.handleChange} required />
                                </form>
                                <button type='submit' form='changeMap' value='Submit'>SUBMIT</button>
                            </div>
                            <div>
                                <span>Goal:</span>
                                <form id='changeGoal' onSubmit={this.changeGoal}>
                                    <span>i:</span>
                                    <input name='goalI' type='number' onChange={this.handleChange} required />
                                    <span>j:</span>
                                    <input name='goalJ' type='number' onChange={this.handleChange} required />
                                </form>
                                <button type='submit' form='changeMap' value='Submit'>SUBMIT</button>
                            </div>
                            <div>
                                <span>Costs:</span>
                                <form id='changeCosts' onSubmit={this.changCosts}>
                                    <span>i:</span>
                                    <input name='inputHeight' type='number' onChange={this.handleChange} required />
                                    <span>j:</span>
                                    <input name='inputWidth' type='number' onChange={this.handleChange} required />
                                </form>
                                <button type='submit' form='changeMap' value='Submit'>SUBMIT</button>
                            </div>
                        </div>
                    </div>
                }
            </div>
            // <div className='Puzzle'>
            //     <Board width={this.state.mapWidth} height={this.state.mapHeight}/>
            //      <form onSubmit={this.handleSubmit}>
            //          <input name='inputWidth' type='number' onChange={this.handleMapChange} required />
            //          {/* <input name='inputHeight' type='number' onChange={this.handleMapChange} required /> */}
            //     </form>
            // </div>
        );
    }
}

export default Puzzle;
