import React from 'react';
import './puzzle.styles.scss';

import Board from '../board/board.component';
import Popup from '../popup/popup.component';

class Puzzle extends React.Component {

    constructor(props) {
        super(props);
        this.state = {
            settings: true,
            mapWidth: 5,
            mapHeight: 5,
            inputWidth: 0,
            inputHeight: 0,
            start: [0, 0],
            goal: [4, 4],
            costs: []
        }
    }

    handleSubmit = (e) => {
        // console.log(e);
        // this.setState({
        //     settings: false,
        //     mapWidth: config.mapWidth,
        //     mapHeight: config.mapHeight,
        //     start: config.start,
        //     goal: config.goal,
        //     costs: config.costs
        // });
        this.setState({mapWidth: inputWidth});
    }

    handleMapChange = e => {
        // console.log(e.target);
        const {name, value} = e.target;
        this.setState({[name]: value});
        // console.log(this.state.mapWidth);
    }

    render() {
        // const width = this.state.mapWidth;
        // const height = this.state.mapHeight;
        return(
            <div className='Puzzle'>
                <Board width={this.state.mapWidth} height={this.state.mapHeight}/>
                <form onSubmit={this.handleSubmit}>
                    <input name='inputWidth' type='number' onChange={this.handleMapChange} required />
                    <input name='inputHeight' type='number' onChange={this.handleMapChange} required />
                </form>
            </div>
        );
    }
}

export default Puzzle;
